/******************************************************************************************************************************************************************************************************\
 ***  FUNCTION:
 ***      Ca49_EEG_RETURN_en
 *** 
 ***  DESCRIPTION:
 ***      Entry function of state: MOTA/MOTA_Chart.Slide.EEG_RETURN
 *** 
 ***  PARAMETERS:
 ***      Type               Name                Description
 ***      ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ***
 ***  RETURNS:
 ***      void
 ***
 ***  SETTINGS:
 ***
\******************************************************************************************************************************************************************************************************/
static void Ca49_EEG_RETURN_en(void)
{
   Ca22_Slide_ns = Ca49_EEG_RETURN_id;
   if (MOTA_Input_Slide.Hall_Slide < (EERAM_MemoDrivePos - MOTA_TOL)) {
      /* State transition to MOTA/MOTA_Chart.Slide.EEG_RETURN.GO_TO_DRIVE_POSITION1 */
      Ca1_CmdMotor_Slide = MOTA_CMD_FORWARD;
      Ca1_AutomaticMov = MOTA_ON;
      Ca49_EEG_RETURN_ns = Ca51_GO_TO_DRIVE_POSITION1_id;
   }
   else {
      if (MOTA_Input_Slide.Hall_Slide > (EERAM_MemoDrivePos + MOTA_TOL)) {
         /* State transition to MOTA/MOTA_Chart.Slide.EEG_RETURN.GO_TO_DRIVE_POSITION */
         Ca1_CmdMotor_Slide = MOTA_CMD_BACKWARD;
         Ca1_AutomaticMov = MOTA_ON;
         Ca49_EEG_RETURN_ns = Ca54_GO_TO_DRIVE_POSITION_id;
      }
      else {
         /* State transition to MOTA/MOTA_Chart.Slide.EEG_RETURN.End */

         /* [(( Hall_Slide >= (EERAM_MemoDrivePos_Read - MOTA_TOL)) ...
            &&  (Hall_Slide <= (EERAM_MemoDrivePos_Read + MOTA_TOL))) ... 
            || (StatusMotor_Slide == MOTA_STS_MECH_STROKE_FORWARD) ] */
         Ca1_CmdMotor_Slide = MOTA_CMD_STOP;
         Ca49_EEG_RETURN_ns = Ca52_End_id;
         Ca1_EEG_End = MOTA_TRUE;
      }
   }
}

/******************************************************************************************************************************************************************************************************\
 ***  FUNCTION:
 ***      Ca57_EEG_GO_en
 *** 
 ***  DESCRIPTION:
 ***      Entry function of state: MOTA/MOTA_Chart.Slide.EEG_GO
 *** 
 ***  PARAMETERS:
 ***      Type               Name                Description
 ***      ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ***
 ***  RETURNS:
 ***      void
 ***
 ***  SETTINGS:
 ***
\******************************************************************************************************************************************************************************************************/
static void Ca57_EEG_GO_en(void)
{
   Ca22_Slide_ns = Ca57_EEG_GO_id;

   /* If the actual position was lost, the slide come back in mech stroke */
   if (MOTA_Input_Common.Actual_Pos_Loss == MOTA_TRUE) {
      /* State transition to MOTA/MOTA_Chart.Slide.EEG_GO.MAP_AUTO_REALIGNEMENT */
      Ca1_CmdMotor_Slide = MOTA_CMD_BACKWARD;
      Ca1_AutomaticMov = MOTA_ON;
      Ca57_EEG_GO_ns = Ca59_MAP_AUTO_REALIGNEMENT_id;
      Ca1_Actual_Pos_Loss_Rec = MOTA_RECOVERY_IN_PROGRESS;
   }
   else {
      if (MOTA_Input_Slide.Hall_Slide >= (MOTA_Input_Common.PillarPosition + MOTA_PILLAR_TOL)) {
         /* State transition to MOTA/MOTA_Chart.Slide.EEG_GO.GO_TO_EEG_POSITION */
         Ca1_CmdMotor_Slide = MOTA_CMD_BACKWARD;
         Ca1_AutomaticMov = MOTA_ON;
         Ca57_EEG_GO_ns = Ca62_GO_TO_EEG_POSITION_id;

         /* MDEN - Issue redmine #3833. Added MOTA_ACTIVE define */
         Ca1_Check_Antipinch = MOTA_ACTIVE;
      }
      else {
         /* State transition to MOTA/MOTA_Chart.Slide.EEG_GO.EEG_POSITION */
         Ca57_EEG_GO_ns = Ca63_EEG_POSITION_id;
         Ca1_EEG_End = MOTA_TRUE;
      }
   }
}

