/******************************************************************************************************************************************************************************************************\
 ***  FUNCTION:
 ***      Ca32_SLIDE_MEM_RECALL_ex
 *** 
 ***  DESCRIPTION:
 ***      Exit function of state: MOTA/MOTA_Chart.Slide.SLIDE_MEM_RECALL
 *** 
 ***  PARAMETERS:
 ***      Type               Name                Description
 ***      ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ***
 ***  RETURNS:
 ***      void
 ***
 ***  SETTINGS:
 ***
\******************************************************************************************************************************************************************************************************/
static void Ca32_SLIDE_MEM_RECALL_ex(void)
{
   if (Ca33_Suspend) {
      Ca33_Suspend = 0;
   }
   else {
      if (Ca34_MEM_RECALL_SLIDE) {
         if (Ca35_wait) {
            Ca35_wait = 0;
         }
         else {
            if (Ca36_Wait_Fwd) {
               Ca36_Wait_Fwd = 0;
            }
         }
         Ca34_MEM_RECALL_SLIDE = 0;
      }
      else {
         if (Ca38_IDLE) {
            Ca38_IDLE = 0;
         }
      }
   }
   Ca1_AutomaticMov = MOTA_OFF;
}

/******************************************************************************************************************************************************************************************************\
 ***  FUNCTION:
 ***      Ca42_INIT_START_ex
 *** 
 ***  DESCRIPTION:
 ***      Exit function of state: MOTA/MOTA_Chart.Slide.INIT_Slide.INIT_START
 *** 
 ***  PARAMETERS:
 ***      Type               Name                Description
 ***      ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ***
 ***  RETURNS:
 ***      void
 ***
 ***  SETTINGS:
 ***
\******************************************************************************************************************************************************************************************************/
static void Ca42_INIT_START_ex(void)
{
   if (Ca43_BACKWARD) {
      Ca41_Check_Return_Position();
      Ca43_BACKWARD = 0;
   }
   else {
      if (Ca44_FORWARD) {
         Ca44_FORWARD = 0;
      }
      else {
         if (Ca45_BACKWARD_1) {
            Ca1_Write_Map = MOTA_NOT_ACTIVE;
            Ca45_BACKWARD_1 = 0;
         }
      }
   }
   Ca42_INIT_START = 0;
}

/******************************************************************************************************************************************************************************************************\
 ***  FUNCTION:
 ***      Ca57_EEG_GO_ex
 *** 
 ***  DESCRIPTION:
 ***      Exit function of state: MOTA/MOTA_Chart.Slide.EEG_GO
 *** 
 ***  PARAMETERS:
 ***      Type               Name                Description
 ***      ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~
 ***
 ***  RETURNS:
 ***      void
 ***
 ***  SETTINGS:
 ***
\******************************************************************************************************************************************************************************************************/
static void Ca57_EEG_GO_ex(void)
{
   switch (Ca57_EEG_GO_ns) {
      case Ca58_Suspend1_id: {
         Ca57_EEG_GO_ns = 0;
         break;
      }
      case Ca59_MAP_AUTO_REALIGNEMENT_id: {
         Ca1_Actual_Pos_Loss_Rec = MOTA_RECOVERY_NOT_IN_PROGRESS;
         Ca57_EEG_GO_ns = 0;
         break;
      }
      case Ca60_End_id: {
         Ca57_EEG_GO_ns = 0;
         break;
      }
      case Ca61_Suspend2_id: {
         Ca57_EEG_GO_ns = 0;
         break;
      }
      case Ca62_GO_TO_EEG_POSITION_id: {
         Ca1_Check_Antipinch = MOTA_NOT_ACTIVE;
         Ca57_EEG_GO_ns = 0;
         break;
      }
      case Ca63_EEG_POSITION_id: {
         Ca57_EEG_GO_ns = 0;
         break;
      }
      default: {
         break;
      }
   }
}


